import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleAnalyze = async () => {
        setLoading(true);
        setError("");
        setResult(null);

        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: "java",
                code: code
            });

            console.log("‚úÖ Response from backend:", response.data);
            setResult(response.data);
        } catch (err) {
            console.error("‚ùå Error analyzing code:", err);
            if (err.response) {
                setError(`Server Error: ${err.response.data}`);
            } else {
                setError(`Network Error: ${err.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    const renderList = (text, fallback) => {
        if (!text || text.trim() === "") return <li>{fallback}</li>;
        return text
            .split('\n')
            .filter(line => line.trim() && !line.includes("Issues") && !line.includes("Suggestions"))
            .map((line, index) => (
                <li key={index}>{line.replace(/^[-‚Ä¢]\s*/, '')}</li>
            ));
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <Editor
                height="400px"
                language="java"
                value={code}
                onChange={(value) => setCode(value)}
                theme="vs-dark"
            />
            <button onClick={handleAnalyze} style={{ marginTop: "10px", padding: "8px 16px" }}>
                Analyze Code
            </button>

            {loading && <p>‚è≥ Analyzing...</p>}
            {error && <p style={{ color: "red" }}>‚ùå {error}</p>}

            {result && (
                <div style={{ marginTop: "20px" }}>
                    <h3>üõ†Ô∏è Issues</h3>
                    <ul>{renderList(result.issues, "No issues found.")}</ul>

                    <h3>üí° Suggestions</h3>
                    <ul>{renderList(result.suggestions, "No suggestions available.")}</ul>

                    {result.fixedCode && (
                        <>
                            <h3>‚úÖ Fixed Code</h3>
                            <pre style={{
                                background: "#1e1e1e",
                                color: "#dcdcdc",
                                padding: "10px",
                                borderRadius: "5px",
                                overflowX: "auto"
                            }}>
                                {result.fixedCode}
                            </pre>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
