import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleAnalyze = async () => {
        setLoading(true);
        setError("");
        setResult(null);

        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: "java",
                code: code
            });

            console.log("‚úÖ Response from backend:", response.data);
            setResult(response.data);
        } catch (err) {
            console.error("‚ùå Error analyzing code:", err);
            if (err.response) {
                setError(`Server Error: ${err.response.data}`);
            } else {
                setError(`Network Error: ${err.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <Editor
                height="400px"
                language="java"
                value={code}
                onChange={(value) => setCode(value)}
                theme="vs-dark"
            />
            <button onClick={handleAnalyze} style={{ marginTop: "10px" }}>
                Analyze Code
            </button>

            {loading && <p>‚è≥ Analyzing...</p>}
            {error && <p style={{ color: "red" }}>‚ùå {error}</p>}

            {result && (
                <div style={{ marginTop: "20px" }}>
                    <h3>üõ†Ô∏è Issues</h3>
                    <ul>
                        {result.issues && result.issues.length > 0 ? (
                            result.issues.map((issue, index) => <li key={index}>{issue}</li>)
                        ) : (
                            <li>No issues found.</li>
                        )}
                    </ul>

                    <h3>üí° Suggestions</h3>
                    <ul>
                        {result.suggestions && result.suggestions.length > 0 ? (
                            result.suggestions.map((s, index) => <li key={index}>{s}</li>)
                        ) : (
                            <li>No suggestions available.</li>
                        )}
                    </ul>

                    <h3>‚úÖ Output</h3>
                    <pre style={{ background: "#f4f4f4", padding: "10px", borderRadius: "4px" }}>
                        {result.unitTest || result.fixedCode || "No output provided."}
                    </pre>
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
