import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleAnalyze = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: 'java',
                code: code
            });
            setResult(response.data);
            setError(null);
        } catch (err) {
            setResult(null);
            setError(err.response?.data || 'Something went wrong');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>üß† AI Code Analyzer</h2>

            <Editor
                height="400px"
                language="java"
                theme="vs-dark"
                value={code}
                onChange={(value) => setCode(value)}
            />

            <button
                onClick={handleAnalyze}
                style={{
                    marginTop: '10px',
                    padding: '10px 20px',
                    fontWeight: 'bold',
                    backgroundColor: '#007bff',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '5px'
                }}
            >
                Analyze Code
            </button>

            {error && (
                <div style={{ marginTop: '20px', color: 'red' }}>
                    ‚ùå Error: {typeof error === 'string' ? error : JSON.stringify(error)}
                </div>
            )}

            {result && (
                <div style={{ marginTop: '20px' }}>
                    <h3>‚ùó Issues</h3>
                    <ul>
                        {result.issues?.length ? (
                            result.issues.map((issue, idx) => <li key={idx}>{issue}</li>)
                        ) : (
                            <li>No issues found</li>
                        )}
                    </ul>

                    <h3>üí° Suggestions</h3>
                    <ul>
                        {result.suggestions?.length ? (
                            result.suggestions.map((suggestion, idx) => <li key={idx}>{suggestion}</li>)
                        ) : (
                            <li>No suggestions available</li>
                        )}
                    </ul>

                    <h3>üîß Fixed Output</h3>
                    <pre
                        style={{
                            backgroundColor: '#272822',
                            color: '#f8f8f2',
                            padding: '15px',
                            borderRadius: '5px',
                            whiteSpace: 'pre-wrap'
                        }}
                    >
                        {result.fixedCode || 'No fixed output returned.'}
                    </pre>
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
