import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleAnalyze = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: 'java',
                code: code
            });
            setResult(response.data);
            setError(null);
        } catch (err) {
            setResult(null);
            setError(err.response?.data || 'Something went wrong');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>üß† AI Code Analyzer</h2>
            <Editor
                height="400px"
                language="java"
                theme="vs-dark"
                value={code}
                onChange={(value) => setCode(value)}
            />
            <button
                onClick={handleAnalyze}
                style={{ marginTop: '10px', padding: '10px 20px', fontWeight: 'bold' }}
            >
                Analyze Code
            </button>

            {error && (
                <div style={{ marginTop: '20px', color: 'red' }}>
                    ‚ùå Server Error: {typeof error === 'string' ? error : JSON.stringify(error)}
                </div>
            )}

            {result && (
                <div style={{ marginTop: '20px' }}>
                    <h3>üîç Issues</h3>
                    <ul>
                        {result.issues && result.issues.length > 0 ? (
                            result.issues.map((issue, idx) => <li key={idx}>{issue}</li>)
                        ) : (
                            <li>No issues found.</li>
                        )}
                    </ul>

                    <h3>üí° Suggestions</h3>
                    <ul>
                        {result.suggestions && result.suggestions.length > 0 ? (
                            result.suggestions.map((suggestion, idx) => <li key={idx}>{suggestion}</li>)
                        ) : (
                            <li>No suggestions available.</li>
                        )}
                    </ul>

                    <h3>üß™ Unit Test</h3>
                    <pre style={{ background: '#1e1e1e', color: 'white', padding: '15px', borderRadius: '5px' }}>
                        {result.unitTest}
                    </pre>
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
