import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleAnalyze = async () => {
        setLoading(true);
        setError("");
        setResult(null);

        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: "java",
                code: code
            });

            console.log("‚úÖ Response from backend:", response.data);
            setResult(response.data);
        } catch (err) {
            console.error("‚ùå Error analyzing code:", err);
            if (err.response) {
                setError(`Server Error: ${err.response.data}`);
            } else {
                setError(`Network Error: ${err.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <Editor
                height="400px"
                language="java"
                value={code}
                onChange={(value) => setCode(value)}
                theme="vs-dark"
            />
            <button onClick={handleAnalyze} style={{ marginTop: "10px" }}>
                Analyze Code
            </button>

            {loading && <p>‚è≥ Analyzing...</p>}
            {error && <p style={{ color: "red" }}>‚ùå {error}</p>}
            {result && (
               <div style={{
    marginTop: "20px",
    background: "#1e1e1e",
    padding: "20px",
    borderRadius: "10px",
    color: "#f1f1f1",
    fontFamily: "Consolas, monospace",
    boxShadow: "0 4px 8px rgba(0,0,0,0.3)"
}}>
    <h3 style={{ color: "#ffd700" }}>üõ†Ô∏è Issues</h3>
    <pre style={{
        background: "#2d2d2d",
        padding: "10px",
        borderRadius: "5px",
        overflowX: "auto",
        whiteSpace: "pre-wrap"
    }}>{JSON.stringify(result.issues, null, 2)}</pre>

    <h3 style={{ color: "#00e676" }}>üí° Suggestions</h3>
    <pre style={{
        background: "#2d2d2d",
        padding: "10px",
        borderRadius: "5px",
        overflowX: "auto",
        whiteSpace: "pre-wrap"
    }}>{JSON.stringify(result.suggestions, null, 2)}</pre>

    <h3 style={{ color: "#64b5f6" }}>‚úÖ Unit Test</h3>
    <pre style={{
        background: "#2d2d2d",
        padding: "10px",
        borderRadius: "5px",
        overflowX: "auto",
        whiteSpace: "pre-wrap"
    }}>{result.unitTest}</pre>
</div>

            )}
        </div>
    );
};

export default CodeEditor;
