import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState("");
    const [result, setResult] = useState(null);
    const [error, setError] = useState("");

    const handleAnalyze = async () => {
        setError("");
        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: "java",
                code: code
            });
            setResult(response.data);
        } catch (err) {
            console.error(err);
            setError("❌ Server Error: " + err?.response?.data || err.message);
        }
    };

    return (
        <div>
            <Editor
                height="400px"
                language="java"
                value={code}
                onChange={(value) => setCode(value)}
            />
            <button onClick={handleAnalyze}>Analyze Code</button>

            {error && <div style={{ color: 'red' }}>{error}</div>}

            {result && (
                <div>
                    <h3>Issues:</h3>
                    <pre>{JSON.stringify(result.issues, null, 2)}</pre>

                    <h3>Suggestions:</h3>
                    <pre>{JSON.stringify(result.suggestions, null, 2)}</pre>

                    <h3>Unit Test:</h3>
                    <pre>{result.unitTest}</pre>
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
