import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import axios from 'axios';

const CodeEditor = () => {
    const [code, setCode] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleAnalyze = async () => {
        setLoading(true);
        setError("");
        setResult(null);

        try {
            const response = await axios.post('http://localhost:8080/api/code/analyze', {
                language: "java",
                code: code
            });

            console.log("✅ Response from backend:", response.data);
            setResult(response.data);
        } catch (err) {
            console.error("❌ Error analyzing code:", err);
            if (err.response) {
                setError(`Server Error: ${err.response.data}`);
            } else {
                setError(`Network Error: ${err.message}`);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ padding: "20px" }}>
            <Editor
                height="400px"
                language="java"
                value={code}
                onChange={(value) => setCode(value)}
                theme="vs-dark"
            />
            <button onClick={handleAnalyze} style={{ marginTop: "10px" }}>
                Analyze Code
            </button>

            {loading && <p>⏳ Analyzing...</p>}
            {error && <p style={{ color: "red" }}>❌ {error}</p>}
            {result && (
                <div style={{ marginTop: "20px" }}>
                    <h3>Issues:</h3>
                    <pre>{JSON.stringify(result.issues, null, 2)}</pre>
                    <h3>Suggestions:</h3>
                    <pre>{JSON.stringify(result.suggestions, null, 2)}</pre>
                    <h3>Unit Test:</h3>
                    <pre>{result.unitTest}</pre>
                </div>
            )}
        </div>
    );
};

export default CodeEditor;
